mapper.select:
select round(AVG(a.FEvaluate),1)  from
(select * from t_evaluate
where FStaffID = #{staffID,jdbcType=VARCHAR}
and DATE_SUB(CURDATE(), INTERVAL 30 DAY) <= FCreateDate
and FEvaluate is not null
) a

select round(AVG(a.FDuration),1)  from
  (select * from t_evaluate
  where FStaffID = #{staffID,jdbcType=VARCHAR}
  and DATE_SUB(CURDATE(), INTERVAL 30 DAY) <= FCreateDate
  and FEvaluate is not null
  ) a
